  console.log(`Successfully used API: ${apiUsed.node}`);
}

// Pass through the successful result
return $input.all().filter(item => !item.json.error);

// Random Voice Selection for TTS
const voices = [
  "Zephyr", "Puck", "Charon", "Kore", "Fenrir", "Leda", "Orus", "Aoede",
  "Callirrhoe", "Autonoe", "Enceladus", "Iapetus", "Umbriel", "Algieba",
  "Despina", "Erinome", "Algenib", "Rasalgethi", "Laomedeia", "Achernar",
  "Alnilam", "Schedar", "Gacrux", "Pulcherrima", "Achird", "Zubenelgenubi",
  "Vindemiatrix", "Sadachbia", "Sadaltager", "Sulafat"
];

// Pick a random voice
const randomVoice = voices[Math.floor(Math.random() * voices.length)];

// Return the existing data plus the selected voice
return items.map(item => ({
  json: {
    ...item.json,
    selected_voice: randomVoice
  }
}));

return Array.from({ length: 8 }, (_, i) => {
  return { json: { index: i + 1 } };
});

const text = $json.cleaned_script;
const chunkSize = 1500;

const regex = new RegExp(`.{1,${chunkSize}}(\\s|$)`, 'g');
const chunks = text.match(regex).filter(x => x.trim() !== '');

return chunks.map((chunk, index) => ({
  json: {
    chunk,
    tone: $json.tone,
    selected_voice: $json.selected_voice,
    part_index: index
  }
}));

const row = $json.row_number;
const partCount =  $items().length;

return [{
  json: {
    path: `/files/Story image/${row}/concat.txt`,
    content: Array.from({ length: partCount }, (_, i) => `file 'audio_${row}_${i}.mp3'`).join('\n'),
    row_number: row
  }
}];

return items.map((item, index) => {
  return {
    json: {
      ...item.json,
      part_index: index
    }
  };
});

const batchSize = 10;
const input = items.map(i => i.json);
const batches = [];

for (let i = 0; i < input.length; i += batchSize) {
  const batch = input.slice(i, i + batchSize);

  // Make each batch a single item with `articles` key holding a table of rows
  batches.push({
    json: {
      articles: batch.map(article => ({
        title: article.title,
        snippet: article.contentSnippet,
        pubDate: article.pubDate,
        link: article.link
      }))
    }
  });
}

return batches;
